Addon.JavascriptInHead:
function handleBlockCheck() {
	if (jQuery('#foInputBlock').is(':checked')) {
		jQuery('.foFormAllowGroups').show();
	} else {
		jQuery('.foFormAllowGroups').hide();
	}
}

jQuery(document).ready(
	function(){
		handleBlockCheck();
		jQuery('#foInputBlock').click(
			function() {
				handleBlockCheck();
			}
		)
	}
)

